    /**
     * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
     * feature (like MySQL or SQLite).
     *
     * This method is not very speedy because it must perform a query first to get
     * the implicated records and then perform the deletes by calling those Peer classes.
     *
     * This method should be used within a transaction if possible.
     *
     * @param      Criteria $criteria
     * @param      PropelPDO $con
     * @return int The number of affected rows (if supported by underlying database driver).
     */
    protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
    {
        // initialize var to track total num of affected rows
        $affectedRows = 0;

        // first find the objects that are implicated by the $criteria
        $objects = twSubscriptionListPeer::doSelect($criteria, $con);
        foreach ($objects as $obj) {


            // delete related twSubscriptionEmail objects
            $criteria = new Criteria(twSubscriptionEmailPeer::DATABASE_NAME);
            
            $criteria->add(twSubscriptionEmailPeer::LIST_ID, $obj->getId());
            $affectedRows += twSubscriptionEmailPeer::doDelete($criteria, $con);

            // delete related twSubscriptionListInvitation objects
            $criteria = new Criteria(twSubscriptionListInvitationPeer::DATABASE_NAME);
            
            $criteria->add(twSubscriptionListInvitationPeer::LIST_ID, $obj->getId());
            $affectedRows += twSubscriptionListInvitationPeer::doDelete($criteria, $con);

            // delete related twSubscriptionMailing objects
            $criteria = new Criteria(twSubscriptionMailingPeer::DATABASE_NAME);
            
            $criteria->add(twSubscriptionMailingPeer::LIST_ID, $obj->getId());
            $affectedRows += twSubscriptionMailingPeer::doDelete($criteria, $con);

            // delete related twSubscriptionMailQueue objects
            $criteria = new Criteria(twSubscriptionMailQueuePeer::DATABASE_NAME);
            
            $criteria->add(twSubscriptionMailQueuePeer::LIST_ID, $obj->getId());
            $affectedRows += twSubscriptionMailQueuePeer::doDelete($criteria, $con);
        }

        return $affectedRows;
    }

