     * @param      PropelPDO $con the connection to use
     * @return twSubscriptionList
     */
    public static function retrieveByPK($pk, PropelPDO $con = null)
    {

        if (null !== ($obj = twSubscriptionListPeer::getInstanceFromPool((string) $pk))) {
            return $obj;
        }

        if ($con === null) {
            $con = Propel::getConnection(twSubscriptionListPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }

        $criteria = new Criteria(twSubscriptionListPeer::DATABASE_NAME);
        $criteria->add(twSubscriptionListPeer::ID, $pk);

        $v = twSubscriptionListPeer::doSelect($criteria, $con);

        return !empty($v) > 0 ? $v[0] : null;
    }

