            if (null === $this->twSubscriptionMailSentsScheduledForDeletion) {
                $this->twSubscriptionMailSentsScheduledForDeletion = clone $this->colltwSubscriptionMailSents;
                $this->twSubscriptionMailSentsScheduledForDeletion->clear();
