        }
        $this->colltwSubscriptionMailQueues = null;
        if ($this->colltwSubscriptionMailSents instanceof PropelCollection) {
            $this->colltwSubscriptionMailSents->clearIterator();
        }
        $this->colltwSubscriptionMailSents = null;
        $this->atwSubscriptionList = null;
