     *
     * @param      Criteria  $criteria
     * @param      PropelPDO $con
     * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
     * @return array           Array of twSubscriptionMessage objects.
     * @throws PropelException Any exceptions caught during processing will be
     *		 rethrown wrapped into a PropelException.
     */
    public static function doSelectJoinAll(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
    {
        $criteria = clone $criteria;

        // Set the correct dbName if it has not been overridden
        if ($criteria->getDbName() == Propel::getDefaultDB()) {
            $criteria->setDbName(self::DATABASE_NAME);
        }

        twSubscriptionMessagePeer::addSelectColumns($criteria);
        $startcol2 = twSubscriptionMessagePeer::NUM_HYDRATE_COLUMNS;

        twSubscriptionMessageTypePeer::addSelectColumns($criteria);
        $startcol3 = $startcol2 + twSubscriptionMessageTypePeer::NUM_HYDRATE_COLUMNS;

        $criteria->addJoin(twSubscriptionMessagePeer::TYPE_ID, twSubscriptionMessageTypePeer::ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BasetwSubscriptionMessagePeer', $criteria, $con);
		}

        $stmt = BasePeer::doSelect($criteria, $con);
        $results = array();

        while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
            $key1 = twSubscriptionMessagePeer::getPrimaryKeyHashFromRow($row, 0);
            if (null !== ($obj1 = twSubscriptionMessagePeer::getInstanceFromPool($key1))) {
                // We no longer rehydrate the object, since this can cause data loss.
                // See http://www.propelorm.org/ticket/509
                // $obj1->hydrate($row, 0, true); // rehydrate
            } else {
                $cls = twSubscriptionMessagePeer::getOMClass();

                $obj1 = new $cls();
                $obj1->hydrate($row);
                twSubscriptionMessagePeer::addInstanceToPool($obj1, $key1);
            } // if obj1 already loaded

            // Add objects for joined twSubscriptionMessageType rows

            $key2 = twSubscriptionMessageTypePeer::getPrimaryKeyHashFromRow($row, $startcol2);
            if ($key2 !== null) {
                $obj2 = twSubscriptionMessageTypePeer::getInstanceFromPool($key2);
                if (!$obj2) {

                    $cls = twSubscriptionMessageTypePeer::getOMClass();

                    $obj2 = new $cls();
                    $obj2->hydrate($row, $startcol2);
                    twSubscriptionMessageTypePeer::addInstanceToPool($obj2, $key2);
                } // if obj2 loaded

                // Add the $obj1 (twSubscriptionMessage) to the collection in $obj2 (twSubscriptionMessageType)
                $obj2->addtwSubscriptionMessage($obj1);
            } // if joined row not null

            $results[] = $obj1;
        }
        $stmt->closeCursor();

        return $results;
    }

