    public static function doUpdate($values, PropelPDO $con = null)
    {
        if ($con === null) {
            $con = Propel::getConnection(twSubscriptionMessagePeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
        }

        $selectCriteria = new Criteria(self::DATABASE_NAME);

        if ($values instanceof Criteria) {
            $criteria = clone $values; // rename for clarity

            $comparison = $criteria->getComparison(twSubscriptionMessagePeer::ID);
            $value = $criteria->remove(twSubscriptionMessagePeer::ID);
            if ($value) {
                $selectCriteria->add(twSubscriptionMessagePeer::ID, $value, $comparison);
            } else {
                $selectCriteria->setPrimaryTableName(twSubscriptionMessagePeer::TABLE_NAME);
            }

        } else { // $values is twSubscriptionMessage object
            $criteria = $values->buildCriteria(); // gets full criteria
            $selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
        }

        // set the correct dbName
        $criteria->setDbName(self::DATABASE_NAME);

        return BasePeer::doUpdate($selectCriteria, $criteria, $con);
